function validaLogin(){if(null==$("#email").val())return!1;email=$("#email").val();var a=/^([\w-]+(?:\.[\w-]+)*)@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$/i;return!!a.test(email)&&(null!=$("#senha").val()&&void executaLogin())}function executaLogin(){$.ajax({type:"POST",url:"app.control/ajax.php",data:{email:$("#email").val(),senha:$("#senha").val(),request:"login"},success:function(a){a.length>1&&alert(a),top.location="home"}})}